		public DataTable SelectCustomerVoiceWithMoreDetail(string strMembershipID)
		{
			string cmdText = " Select A.*, B.strDescription as InvolveDepartment, " + 
							" C.strDescription as CaseTypeDescription, D.strDescription as CaseCatgoryDesciption, "+
							" E.strEmployeeName as SubmittedByName, " +
							" F.strDescription as AssignedDepartment, " +
							" G.strEmployeeName as CaseName, " + 
							" H.strEmployeeName as LastEditName " +
							" from tblCase A Left Outer Join tblDepartment B on A.nDepartmentID  = B.nDepartmentID " + 
							" Left Outer Join tblCaseType C on A.nTypeID = C.nTypeID " + 
							" Left Outer Join tblCaseCategory D on A.nCategoryID = D.nCategoryID " +
							" Left Outer Join tblEmployee E on A.nSubmittedByID = E.nEmployeeID " + 
							" Left Outer Join tblDepartment F on A.nDepartmentAssignedID = F.nDepartmentID " +
							" Left Outer Join tblEmployee G on A.nStaffID = G.nEmployeeID " +
							" Left Outer Join tblEmployee H on A.nEmployeeID = H.nEmployeeID " +
							" Where strMembershipID = @strMembershipID ";

			return base.LoadData(cmdText, new string [] {"@strMembershipID"}, new object [] {strMembershipID});

		}

