					throw new ArgumentOutOfRangeException("StrPackageCode", "StrPackageCode can't be NULL");
				}
				_strPackageCode = value;
			}
		}


		public SqlBoolean FAllowDiscount
		{
			get
			{
				return _fAllowDiscount;
			}
			set
			{
				_fAllowDiscount = value;
			}
		}
		#endregion
	}
