		public decimal GetMemberLoyaltyPoint(string strMembershipID)
		{
			object obj = base.ExecuteScalar("Select Sum(dRewardsPoints) as TotalRewards From tblRewardsTransaction " +
                " Where strMembershipID = @strMembershipID And nTypeID <> 3  group by strMembershipID",
				new string[] {"@strMembershipID"}, new object[] {strMembershipID});
			
			if (obj != null)
			{
				decimal reward = (decimal)((double)obj);
				return reward;
			}
			return 0;
		}

		public int GetMemberRedemptionID(string strMembershipID)
		{
			object obj = base.ExecuteScalar("Select  max(nTransactionID)+1 From tblRewardsTransaction ",
				new string[] {"@strMembershipID"}, new object[] {strMembershipID});
			
			if (obj.ToString() != string.Empty)
			{
				int reward = (int)((int)obj);
				return reward;
			}
			return 0;
		}

        public string GetSignaturePath(int nTransactionID)
        {
            string strSigPdfPath = "";

            SqlCommand cmdToExecute = new SqlCommand();
            cmdToExecute.CommandText = @"SELECT ISNULL(strSigPdfPath, '') AS strSigPdfPath 
                                            FROM tblRewardsTransaction WHERE nTransactionID = @nTransactionID";
            cmdToExecute.CommandType = CommandType.Text;
            cmdToExecute.Connection = _mainConnection;
            cmdToExecute.Parameters.AddWithValue("@nTransactionID", nTransactionID);

            try
            {
                if (_mainConnectionIsCreatedLocal)
                {
                    _mainConnection.Open();
                }
                else
                {
                    if (_mainConnectionProvider.IsTransactionPending)
                    {
                        cmdToExecute.Transaction = _mainConnectionProvider.CurrentTransaction;
                    }
                }
                SqlDataReader dr = cmdToExecute.ExecuteReader();
                if (dr.HasRows)
                {
                    while (dr.Read())
                    {
                        strSigPdfPath = dr["strSigPdfPath"].ToString().Replace("\\", "\\");
                    }
                }
                dr.Close();
            }
            catch
            {
                strSigPdfPath = "";
            }
            finally
            {
                if (_mainConnectionIsCreatedLocal)
                {
                    _mainConnection.Close();
                }
                cmdToExecute.Dispose();
            }
            return strSigPdfPath;
        }


